import React, { useState } from "react";

export default function NoteCard({
  note,
  categories,
  onArchive,
  onDelete,
  onEdit,
  onAssignCategory,
  onRemoveCategory
}) {
  const [showAssign, setShowAssign] = useState(false);
  const [showRemove, setShowRemove] = useState(false);
  const [selectedAssign, setSelectedAssign] = useState("");
  const [selectedRemove, setSelectedRemove] = useState("");

  return (
    <div style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px" }}>
      <h3>{note.Title}</h3>
      <p>{note.Content}</p>
      <p>
        Categories: {note.Categories?.map(c => c.Name).join(", ") || "None"}
      </p>

      <button onClick={() => onEdit(note)}>Edit</button>
      <button onClick={() => onArchive(note.ID)}>
        {note.Archived ? "Unarchive" : "Archive"}
      </button>
      <button onClick={() => onDelete(note.ID)}>Delete</button>

      {/* Agregar categoría */}
      <div>
        <button onClick={() => setShowAssign(!showAssign)}>Add Category</button>
        {showAssign && (
          <div>
            <select value={selectedAssign} onChange={(e) => setSelectedAssign(e.target.value)}>
              <option value="">Select category</option>
              {categories
                .filter(c => !note.Categories?.some(nc => nc.ID === c.ID))
                .map(c => (
                  <option key={c.ID} value={c.ID}>{c.Name}</option>
                ))}
            </select>
            <button onClick={() => {
              if (selectedAssign) onAssignCategory(note.ID, parseInt(selectedAssign));
              setSelectedAssign("");
            }}>Add</button>
          </div>
        )}
      </div>

      {/* Quitar categoría */}
      <div>
        <button onClick={() => setShowRemove(!showRemove)}>Remove Category</button>
        {showRemove && (
          <div>
            <select value={selectedRemove} onChange={(e) => setSelectedRemove(e.target.value)}>
              <option value="">Select category</option>
              {note.Categories?.map(c => (
                <option key={c.ID} value={c.ID}>{c.Name}</option>
              ))}
            </select>
            <button onClick={() => {
              if (selectedRemove) onRemoveCategory(note.ID, parseInt(selectedRemove));
              setSelectedRemove("");
            }}>Remove</button>
          </div>
        )}
      </div>
    </div>
  );
}
