import React, { useEffect, useState } from "react";
import {
  getNotes,
  toggleArchive,
  deleteNote,
  createNote
} from "../services/api";
import NoteCard from "./NoteCard";

function NotesList() {
  const [notes, setNotes] = useState([]); // Estado para holdear notas
  const [loading, setLoading] = useState(true); // Estado para mostrar la carga

  // Fetch de notas del backend cuando se monta el componente
  useEffect(() => {
  async function fetchNotes() {
    try {
      const data = await getNotes();
      setNotes(data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    } finally {
      setLoading(false);
    }
  }

  fetchNotes();
}, []);

  // Función para crear nota simple
  const handleCreateNote = async () => {
    const title = prompt("Enter note title:");
    const content = prompt("Enter note content:");
    if (!title || !content) return;
    try {
      const newNote = await createNote({ Title: title, Content: content });
      setNotes([...notes, newNote]);
    } catch (error) {
      console.error("Failed to create note:", error);
    }
  };


  // Archivar o Desarchivar nota
  const handleToggleArchive = async (id) => {
    try {
      const updatedNote = await toggleArchive(id);
      setNotes(notes.map(n => (n.ID === updatedNote.ID ? updatedNote : n)));
    } catch (error) {
      console.error("Failed to toggle archive:", error);
    }
  };

  // Borrar nota
  const handleDelete = async (id) => {
    try {
      await deleteNote(id);
      setNotes(notes.filter(n => n.ID !== id));
    } catch (error) {
      console.error("Failed to delete note:", error);
    }
  };

  // Editar (simplificada, después podemos agregar formulario)
  function handleEdit(note) {
  alert("Edit feature coming soon: " + note.Title);
  }

  if (loading) return <p>Loading notes...</p>;

  return (
  <div>
    <button onClick={handleCreateNote} style={{ marginBottom: "15px" }}>
    Create Note
    </button>
    <h1>Notes</h1>
    {notes.map(note => (
      <NoteCard
        key={note.ID}
        note={note}
        onArchive={handleToggleArchive}
        onDelete={handleDelete}
        onEdit={handleEdit}
      />
    ))}
  </div>
);
}

export default NotesList;
