const API_BASE = "http://localhost:8080";

// Notas
export async function getNotes() {
  const res = await fetch(`${API_BASE}/notes`);
  if (!res.ok) throw new Error("Failed to get notes");
  return res.json();
}

export async function getArchivedNotes() {
  const res = await fetch(`${API_BASE}/notes/archived`);
  if (!res.ok) throw new Error("Failed to get archived notes");
  return res.json();
}

export async function createNote(payload) {
  const res = await fetch(`${API_BASE}/notes`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(payload),
  });
  if (!res.ok) throw new Error("Failed to create note");
  return res.json();
}

export async function updateNote(id, payload) {
  const res = await fetch(`${API_BASE}/notes/${id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(payload),
  });
  if (!res.ok) throw new Error("Failed to update note");
  return res.json();
}

export async function deleteNote(id) {
  const res = await fetch(`${API_BASE}/notes/${id}`, { method: "DELETE" });
  if (!res.ok) throw new Error("Failed to delete note");
  return res.json();
}

export async function toggleArchive(id) {
  // Si tu endpoint usa PATCH como comentamos, usamos PATCH
  const res = await fetch(`${API_BASE}/notes/${id}/archive`, { method: "PATCH" });
  if (!res.ok) throw new Error("Failed to switch archived state");
  return res.json();
}

// Categorías
export async function getCategories() {
  const res = await fetch(`${API_BASE}/categories`);
  if (!res.ok) throw new Error("Failed to get categories");
  return res.json();
}

export async function createCategory(payload) {
  const res = await fetch(`${API_BASE}/categories`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(payload),
  });
  if (!res.ok) throw new Error("Failed to create category");
  return res.json();
}

export async function assignCategory(noteId, categoryId) {
  const res = await fetch(`${API_BASE}/notes/${noteId}/category/${categoryId}`, { method: "PUT" });
  if (!res.ok) throw new Error("Failed to assign category");
  return res.json();
}

export async function removeCategory(noteId) {
  const res = await fetch(`${API_BASE}/notes/${noteId}/category`, { method: "DELETE" });
  if (!res.ok) throw new Error("Failed to remove category");
  return res.json();
}

export async function getNotesByCategory(categoryId) {
  const res = await fetch(`${API_BASE}/categories/${categoryId}/notes`);
  if (!res.ok) throw new Error("Failed to get notes by category");
  return res.json();
}
