import React, { useState } from "react";

export default function NoteCard({ note, onArchive, onDelete, onEdit, onAssignCategory, onRemoveCategory, categories }) {
    const [selectedCategory, setSelectedCategory] = useState("");

    return (
    <div style={{
      border: "1px solid #ccc",
      padding: "10px",
      marginBottom: "10px",
      borderRadius: "5px"
    }}>
      <h3>{note.Title}</h3>
      <p>{note.Content}</p>
      <p>
        <strong>Status:</strong> {note.Archived ? "Archived" : "Active"}
      </p>

      <p>
        <strong>Categories:</strong> {note.Categories?.map(c => c.name).join(", ") || "None"}
     </p>

      <button onClick={() => onEdit(note)}>Edit</button>
      <button onClick={() => onArchive(note.ID)}>
        {note.Archived ? "Unarchive" : "Archive"}
      </button>
      <button onClick={() => onDelete(note.ID)}>Delete</button>


    <div>
        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">Select category</option>
          {categories.map((c) => (
            <option key={c.ID} value={c.ID}>{c.name}</option>
          ))}
        </select>
        <button onClick={() => onAssignCategory(note.ID, selectedCategory)}>Assign Category</button>
      </div>

      {note.Category && (
        <button onClick={() => onRemoveCategory(note.ID)}>Remove Category</button>
      )}
    </div>
  );
}
