import React, { useEffect, useState } from "react";
import {
  getNotes,
  toggleArchive,
  deleteNote,
  createNote,
  getArchivedNotes,
  updateNote,
  getCategories,
  createCategory,
  assignCategory,
  removeCategory,
  getNotesByCategory
} from "../services/api";
import NoteCard from "./NoteCard";

function NotesList() {
  const [notes, setNotes] = useState([]); // Estado para holdear notas
  const [loading, setLoading] = useState(true); // Estado para mostrar la carga
  const [categories, setCategories] = useState([]);
  const [filterCategory, setFilterCategory] = useState("");


  // Fetch de notas del backend cuando se monta el componente
  useEffect(() => {
  async function fetchNotes() {
    try {
      const data = await getNotes();
      setNotes(data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    } finally {
      setLoading(false);
    }
  }

  fetchNotes();
}, []);

//Fetch de categorias del backend cuando se monta el componente
useEffect(() => {
  async function fetchCategories() {
    try {
      const data = await getCategories(); // Función que debemos definir en api.js
      setCategories(data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  }

  fetchCategories();
}, []);

  // Función para crear nota simple
  const handleCreateNote = async () => {
    const title = prompt("Enter note title:");
    const content = prompt("Enter note content:");
    if (!title || !content) return;
    try {
      const newNote = await createNote({ Title: title, Content: content });
      setNotes([...notes, newNote]);
    } catch (error) {
      console.error("Failed to create note:", error);
    }
  };


  // Archivar o Desarchivar nota
  const handleToggleArchive = async (id) => {
    try {
      const updatedNote = await toggleArchive(id);
      setNotes(notes.map(n => (n.ID === updatedNote.ID ? updatedNote : n)));
    } catch (error) {
      console.error("Failed to toggle archive:", error);
    }
  };

  // Borrar nota
  const handleDelete = async (id) => {
    try {
      await deleteNote(id);
      setNotes(notes.filter(n => n.ID !== id));
    } catch (error) {
      console.error("Failed to delete note:", error);
    }
  };

  // Editar (simplificada, después podemos agregar formulario)
  function handleEdit(note) {
  alert("Edit feature coming soon: " + note.Title);
  }

  // Asignar categoria
  const handleAssignCategory = async (noteId, categoryId) => {
  try {
    const updatedNote = await assignCategory(noteId, categoryId); // función en api.js
    setNotes(notes.map(n => n.ID === updatedNote.ID ? updatedNote : n));
  } catch (error) {
    console.error("Failed to assign category:", error);
  }
};

// Remover categoria
const handleRemoveCategory = async (noteId) => {
  try {
    const updatedNote = await removeCategory(noteId); // función en api.js
    setNotes(notes.map(n => n.ID === updatedNote.ID ? updatedNote : n));
  } catch (error) {
    console.error("Failed to remove category:", error);
  }
};

// Filtrar notas por categoria
const filteredNotes = filterCategory
  ? notes.filter(n => n.Categories?.some(c => c.ID === parseInt(filterCategory)))
  : notes;



  if (loading) return <p>Loading notes...</p>;

  return (
  <div>
    <button onClick={handleCreateNote} style={{ marginBottom: "15px" }}>
    Create Note
    </button>
    <h1>Notes</h1>
    {notes.map(note => (
  <NoteCard
    key={note.ID}
    note={note}
    onArchive={handleToggleArchive}
    onDelete={handleDelete}
    onEdit={handleEdit}
    onAssignCategory={handleAssignCategory} // nueva función
    onRemoveCategory={handleRemoveCategory} // nueva función
    categories={categories} // <- esto es lo que faltaba
  />
))}

  </div>
);
}

export default NotesList;
