import React, { useState } from "react";

export default function NoteCard({ note, onArchive, onDelete, onEdit, onAssignCategory, onRemoveCategory, categories }) {
  const [showRemoveSelect, setShowRemoveSelect] = useState(false);
  const [selectedRemoveCategory, setSelectedRemoveCategory] = useState("");
  const [selectedAssignCategory, setSelectedAssignCategory] = useState("");

  return (
    <div style={{
      border: "1px solid #ccc",
      padding: "10px",
      marginBottom: "10px",
      borderRadius: "5px"
    }}>
      <h3>{note.Title}</h3>
      <p>{note.Content}</p>
      <p><strong>Status:</strong> {note.Archived ? "Archived" : "Active"}</p>

      {note.Categories && note.Categories.length > 0 && (
        <p>
          <strong>Categories:</strong> {note.Categories.map(c => c.name).join(", ")}
        </p>
      )}

      {/* Botones básicos */}
      <button onClick={() => onEdit(note)}>Edit</button>
      <button onClick={() => onArchive(note.ID)}>
        {note.Archived ? "Unarchive" : "Archive"}
      </button>
      <button onClick={() => onDelete(note.ID)}>Delete</button>

      {/* Asignar categoría */}
      <div>
        <select value={selectedAssignCategory} onChange={(e) => setSelectedAssignCategory(e.target.value)}>
          <option value="">Select category</option>
          {categories.map((c) => (
            <option key={c.ID} value={c.ID}>{c.name}</option>
          ))}
        </select>
        <button
          onClick={() => {
            if (selectedAssignCategory) onAssignCategory(note.ID, selectedAssignCategory);
          }}
        >
          Add Category
        </button>
      </div>

      {/* Remover categoría */}
      <div>
        <button onClick={() => setShowRemoveSelect(!showRemoveSelect)}>
          Remove Category
        </button>

        {showRemoveSelect && note.Categories && note.Categories.length > 0 && (
          <div style={{ marginTop: "5px" }}>
            <select
              value={selectedRemoveCategory}
              onChange={(e) => setSelectedRemoveCategory(e.target.value)}
            >
              <option value="">Select category</option>
              {note.Categories.map((c) => (
                <option key={c.ID} value={c.ID}>{c.name}</option>
              ))}
            </select>
            <button
              onClick={() => {
                const categoryName = note.Categories.find(c => c.ID === parseInt(selectedRemoveCategory))?.name;
                if (selectedRemoveCategory) {
                  alert(`Remove category: ${categoryName} from note: ${note.Title}`);
                  onRemoveCategory(note.ID, selectedRemoveCategory);
                  setSelectedRemoveCategory("");
                  setShowRemoveSelect(false);
                }
              }}
            >
              Confirm Remove
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
