import React, { useState } from "react";

export default function NoteCard({ note, onArchive, onDelete, onEdit, onAssignCategory, onRemoveCategory, categories }) {
    const [selectedCategory, setSelectedCategory] = useState("");

     return (
    <div style={{
      border: "1px solid #ccc",
      padding: "10px",
      marginBottom: "10px",
      borderRadius: "5px"
    }}>
    <h3>{note.Title}</h3>
    <p>{note.Content}</p>
    <p><strong>Status:</strong> {note.Archived ? "Archived" : "Active"}</p>

    <p><strong>Categories:</strong> {note.Categories?.map(c => c.name).join(", ") || "None"}</p>

    <div>
        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
            <option value="">Select category</option>
            {categories.map(c => (
            <option key={c.ID} value={c.ID}>{c.name}</option>
            ))}
        </select>

        <button
            onClick={() => {
            if (selectedCategory) onAssignCategory(note.ID, selectedCategory);
            }}
        >
            Add Category
        </button>

    </div>

    <div>
        <label>Remove category:</label>
        <select
            value={selectedRemoveCategory}
            onChange={(e) => setSelectedRemoveCategory(e.target.value)}
        >
            <option value="">Select category</option>
            {note.Categories?.map((c) => (
            <option key={c.ID} value={c.ID}>{c.name}</option>
            ))}
        </select>
        <button
            onClick={() => {
            if (selectedRemoveCategory) {
                onRemoveCategory(note.ID, selectedRemoveCategory);
                setSelectedRemoveCategory(""); // limpiar selección después
            }
            }}
        >
            Remove Category
        </button>
    </div>


    <div>
        {note.Categories?.map(c => (
            <button key={c.ID} onClick={() => onRemoveCategory(note.ID, c.ID)}>
            Remove {c.name}
            </button>
        ))}
    </div>

        <button onClick={() => onEdit(note)}>Edit</button>
        <button onClick={() => onArchive(note.ID)}>{note.Archived ? "Unarchive" : "Archive"}</button>
        <button onClick={() => onDelete(note.ID)}>Delete</button>
    </div>
  );
}
